INSERT INTO `wp_term_taxonomy` (`term_taxonomy_id`, `term_id`, `taxonomy`, `description`, `parent`, `count`) VALUES
(1, 1, 'category', 'Meets occur on the penultimate Saturday of every month and are full-day affairs, usually having a defined plan and running for over ten hours in length with a private hired venue. Episode viewings, music, competitions and games are common here. These meets typically draw a large crowd from further afield than just Bristol, and make a good introduction for your first brony meet.', 0, 17),
(2, 2, 'nav_menu', '', 0, 4),
(3, 3, 'nav_menu', '', 0, 2),
(4, 4, 'nav_menu', '', 0, 2),
(5, 5, 'nav_menu', '', 0, 4),
(6, 6, 'category', 'Our weekly socials (sometimes referred to as mini meets) take place on Fridays and are about three hours long. These are intended to be short, informal gatherings of local bronies and rarely have any defined plan.', 0, 70),
(7, 7, 'category', 'Occasionally we hold special meets, which are based around specific events such as local festivals and concerts, or just meets which have greater focus on special features such as tabletop gaming or cosplay.', 0, 5),
(8, 8, 'category', 'Meets for film fans, fanatics and general appreciators. Bring on the popcorn! ', 0, 5),
(9, 9, 'category', 'Becauserarelywe find a reason to leave Bristol.', 0, 0);
